        ele.position.y += sy;
        this.dataContainer.addChild(ele);
        ele = textArr[2];
        sX += this.tableCoord.w / 2.34;
        ele.position.x += sX;
        ele.position.y += sy;
        this.dataContainer.addChild(ele);
        ele = textArr[3];
        sX += this.tableCoord.w / 6;
        ele.position.x += sX;
        ele.position.y += sy;
        this.dataContainer.addChild(ele);
        ele = textArr[4];
        sX += this.tableCoord.w / 5.3;
